import { React, useEffect } from "react";
import "./explore.css";
import { Link } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";
import ImageIcon from '@mui/icons-material/Image';

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  flexDirection: "column",
  transform: "translate(-50%, -50%)",
  width: 300,
  height: 300,
  bgcolor: "black",
  border: "1px solid orangered",
  boxShadow: 24,
  p: 4,
  color: "white",
  borderRadius: "12px",
};

const Explore = () => {
  var tokens = [];

  const [nfts, setNfts] = useState([]);
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  useEffect(async () => {
    const response = await axios.get(`http://localhost:8080/getTokens`, {});
    response.data.result.reverse();

    for (var i = 0; i < response.data.result.length; i++) {
      try {
        const uri = await axios.get(`http://localhost:8080/getTokenURI`, {
          params: { tokenId: response.data.result[i] },
        });
        const uriResponse = await axios.get(uri.data.result);
        const price = await axios.get(`http://localhost:8080/getTokenPrice`, {
          params: { tokenId: response.data.result[i] },
        });

        tokens.push({
          ...uriResponse.data,
          tokenId: response.data.result[i],
          price: price.data.result,
        });
      } catch (err) {}
    }

    setNfts(tokens);

  }, []);

  return (
    <>
      <ul
        style={{
          paddingLeft: "120px",
          marginTop: "50px",
          textDecoration: "none",
        }}
      >
        {" "}
        <li>
          <h1>Explore</h1>
        </li>
      </ul>

      <div id="containerStyle">
        {nfts.map((value, index) => {
          return (
            <div className="card" key={index}>
              <Link to={`/details/${value.tokenId}`}>
                <div className="upperSection">
                  <video
                    src={value.media}
                    preload="auto|metadata|none"
                    className="vid"
                    style={{ width: "268px" }}
                  />
                </div>
                <div className="lowerSection">
                  <img src="https://picsum.photos/50/50" />
                  <h4>Tenz</h4>
                  <p>
                    Price:{" "}
                    <span style={{ color: "orangered", fontWeight: "bold" }}>
                      {value.price / Math.pow(10, 18)} Tokens
                    </span>
                  </p>
                </div>

                <h2> {value.title}</h2>
              </Link>
            </div>
          );
        })}
    <button onClick={()=>{handleOpen()}}>Open modal</button>
    <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>

      

          <h1>Register Profile</h1>
          {/* <span>{walletAddress}</span>     */}
          {/* <input type="file" backgroundImage={ImageIcon}
           style={{ width: "255px"}}
           placeholder="Profile Image"
          /> */}

        <label for="inputTag" back>
          Select Image
          <i backgroundImage={ImageIcon}></i>
          <input style={{display: "none"}} id="inputTag" type="file"/>
        </label>

            <input
                type="number"
                placeholder="Enter Name"
                style={{ width: "255px"}}
              />


        </Box>
      </Modal>

      </div>
    </>
  );
};

export default Explore;
