import { React, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./sale.css";

const Sale = () => {

  const [auctionTokens, setAuctionTokens] = useState([]);
  const [completedAuctions, setCompletedAuctions] = useState([]);
  var tokens = [];
  var completed = []
  
  let getTokenURI = async()=>{
    const res = await axios.get(`http://localhost:8080/getAllAuctions`);

    for (let i = 0; i < res.data.result.length; i++) {

      const uri = await axios.get(
        `http://localhost:8080/getTokenURI`,
        {
            params: { tokenId: res.data.result[i] },
        }
    );
    const tokenResponse = await axios.get(uri.data.result);
      
    }
  }

  let timeFormatter = (seconds) => {
    const format = (val) => `0${Math.floor(val)}`.slice(-2);
    const hours = seconds / 3600;
    const minutes = (seconds % 3600) / 60;

    return `${format(hours)}h ${format(minutes)}m ${format(seconds % 60)}s`;
};

  useEffect(async()=>{

    const res = await axios.get(`http://localhost:8080/getAllAuctions`);
    
    for (let i = 0; i < res.data.result.length; i++) {
      var auctionInfo = null;
      try {
          auctionInfo = await axios.get(
              `http://localhost:8080/auctionInfo`,
              {
                  params: { tokenId: res.data.result[i] },
              }
          );
      } catch (_) {
          continue;
      }
      const uri = await axios.get(
          `http://localhost:8080/getTokenURI`,
          {
              params: { tokenId: res.data.result[i] },
          }
      );
      const tokenResponse = await axios.get(uri.data.result);
      var diff =
          parseInt(auctionInfo.data.result.tknEndTime) -
          Math.floor(Date.now() / 1000);
     if(diff>0)
     {
      var formattedTime = timeFormatter(diff);
      tokens.push({
          ...auctionInfo.data.result,
          ...tokenResponse.data,
          tknEndTime: formattedTime,
      });
     }
     else if(diff<0){
      completed.push({
        ...auctionInfo.data.result,
        ...tokenResponse.data,
        tknEndTime: formattedTime,
    });
     }
      
  }

  setAuctionTokens(tokens)
  setCompletedAuctions(completed)

  });

  console.log(completed);

  return (
    <>

          <h1>On Sale</h1>

      <div id="containerStyle">
        {auctionTokens.map((value, index) => {
          return (
            <div className="card" key={index}>
              <Link to={`/details/${value.tokenId}`}>
                <div className="upperSection">
                  <video
                    src={value.media}
                    preload="auto|metadata|none"
                    className="vid"
                    style={{ width: "268px" }}
                  />
                </div>
                <div className="lowerSection">
                  <img src="https://picsum.photos/50/50" />
                  <h4>Tenz</h4>
                  <p>
                    Price:{" "}
                    <span style={{ color: "orangered", fontWeight: "bold" }}>
                      {value.tknBid / Math.pow(10, 18)} Tokens
                    </span>
                  </p>
                </div>

                <h2> {value.title}</h2>

                <div className="tokenEndTime">
                  <p> {value.tknEndTime} <span style={{color: "white"}}>Left</span></p>
                </div>

              </Link>
            </div>
          );
        })}
      </div>

    <h1>Completed Auctions</h1>
    </>
  )
}

export default Sale
