import { React, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";


const Sale = () => {

  const [auctionTokens, setAuctionTokens] = useState([]);

  useEffect(async()=>{

    const getAuctened = await axios.get(`http://localhost:8080/getAllAuctions`);
    setAuctionTokens(getAuctened.data.result);
    //we got the tokens
    // Now get the uris
    // get auction info
  


  });

  console.log(auctionTokens);

  return (
    <>

          <h1>On Sale</h1>

      <div id="containerStyle">
        {auctionTokens.map((value, index) => {
          return (
            <div className="card" key={index}>
              <Link to={`/details/${value.tokenId}`}>
                <div className="upperSection">
                  <video
                    src={value.media}
                    preload="auto|metadata|none"
                    className="vid"
                    style={{ width: "268px" }}
                  />
                </div>
                <div className="lowerSection">
                  <img src="https://picsum.photos/50/50" />
                  <h4>Tenz</h4>
                  <p>
                    Price:{" "}
                    <span style={{ color: "orangered", fontWeight: "bold" }}>
                      {value.price / Math.pow(10, 18)} Tokens
                    </span>
                  </p>
                </div>

                <h2> {value.title}</h2>
              </Link>
            </div>
          );
        })}
      </div>

    <h1>Completed Auctions</h1>
    </>
  )
}

export default Sale
