import { React, useEffect, useState } from "react";
import axios from "axios";
import "./creators.css";

const Creators = () => {
  var ids = [];
  const [profilesData, setProfileData] = useState();

  useEffect(async () => {
    const res = await axios.get("http://localhost:8080/getProfile", {});

    for (let i = 0; i < res.data.length; i++) {
      ids.push({
        name: res.data[i].name,
        address: res.data[i].address,
        image: res.data[i].profileImg.data.data.toString("base-64"),
      });
    }
    setProfileData(ids);
  }, []);

  console.log(profilesData[0].name);


  return (
    <div className="mainDiv">
      <h1>Creators</h1>

      <div className=" populateDiv">
        {profilesData.map((value) => {
          <div className="creatorCards">
            <img src="https://picsum.photos/200/300" />
            <div className="creatorInfo">
              <h3>Name:{value.name}</h3>
              <h4>Address:{value.address}</h4>
            </div>
          </div>;
        })}
      </div>
    </div>
  );
};

export default Creators;
