import React, { useState } from "react";
import axios from "axios";
import Spinner from ".././Spinner/Spinner";
import "./transactions.css";

const Transactions = () => {
  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();
  const [isLoading, setIsLoading] = useState(false);
  const [nfts, setNfts] = useState([]);

  var tokens = [];
  const find = async() =>{
    setIsLoading(true);
    var startTimestamp = new Date(startDate).getTime() / 1000;
    var endTimestamp = new Date(endDate).getTime() / 1000;

    const res = await axios.get(`${process.env.REACT_APP_API_URL}/getHistoricalNFT`);

      for(var i =0; i< [...res.data.result].length; i++){

        if([...res.data.result][i].returnValues.timestamp>=startTimestamp &&
          [...res.data.result][i].returnValues.timestamp<=endTimestamp 
          )
          tokens.push([...res.data.result][i].returnValues.tokenId);
          const uri = await axios.get(
            `${process.env.REACT_APP_API_URL}/getTokenURI`,
            {
              params: { tokenId: [...res.data.result][i].returnValues.tokenId },
            }
          );
          const uriResponse = await axios.get(uri.data.result);
          const price = await axios.get(
            `${process.env.REACT_APP_API_URL}/getTokenPrice`,
            {
              params: { tokenId: [...res.data.result][i].returnValues.tokenId},
            }
          );
  
          const owner = await axios.get(
            `${process.env.REACT_APP_API_URL}/ownerOf`,
            {
              params: { tokenId: [...res.data.result][i].returnValues.tokenId},
            }
          );
  
          const profile = await axios.get(
            `${process.env.REACT_APP_API_URL}/getProfileInfo`,
            {
              params: {
                address: owner.data.result.toLowerCase(),
              },
            }
          );
          tokens.push({
            ...uriResponse.data,
            tokenId: [...res.data.result][i].returnValues.tokenId,
            price: price.data.result,
            name: profile.data[0].name,
            image: profile.data[0].profileImg,
          });
      }
      setNfts(tokens);
      setIsLoading(false);
  }

  return (
    <div className="transactionsDiv">
      <div className="findBetweenDates">
       <center> <h2>Select Dates to find NFTs</h2></center>
        <input
         type="text"
          onChange={(e) => setStartDate(e.target.value)}
          onFocus={(e) => (e.target.type = "date")}
          onBlur={(e) => (e.target.type = "text")}
          placeholder="Enter Start Date" 
          min={Date.now()}
        />

        <input
          type="text"
          onChange={(e) => setEndDate(e.target.value)}
          onFocus={(e) => (e.target.type = "date")}
          onBlur={(e) => (e.target.type = "text")}
          placeholder="Enter End Date" 
          min={Date.now()}
        />
      </div>
      <button className="findButton" onClick={find}>Find</button>

      {isLoading ? (
          <Spinner />
        ) : (
          nfts.map((value, index) => {
            return (
              <div className="card" key={index}>
                <Link to={`/details/${value.tokenId}`}>
                  <div className="upperSection">
                    <video
                      src={value.media}
                      preload="auto|metadata|none"
                      className="vid"
                      style={{ width: "268px" }}
                    />
                  </div>
                  <div className="lowerSection">
                    <img src={`${value.image}`} />
                    <h4>{value.name}</h4>
                    <p>
                      Price:{" "}
                      <span style={{ color: "orangered", fontWeight: "bold" }}>
                        {value.price / Math.pow(10, 18)} Tokens
                      </span>
                    </p>
                  </div>

                  <h2 className="title"> {value.title}</h2>
                </Link>
              </div>
            );
          })
        )}



    </div>
  );
};

export default Transactions;
