import { React, useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import axios from "axios";
import "./navbar.css";

const Navbar = () => {
  const [walletAddress, setWalletAddress] = useState("");
  const [walletBalance, setWalletBalance] = useState(0);

  window.ethereum.on("accountsChanged", () => {
    isWalletConnected();
  });

  const isWalletConnected = async() => {
    if (!window.ethereum) {
      alert("Install Metamask");
    }
    const chain = await window.ethereum.request({ method: "net_version" });
    if(chain != "3"){
      alert("change Chain to Ropsten Testnet");
      window.location.reload();
    }
    let address = window.ethereum.selectedAddress;
    localStorage.setItem("walletAddress", address);

    setWalletAddress(address ? address.toString() : "");
  };

  const connectWalletHandler = async () => {
    const { ethereum } = window;

    if (!ethereum) {
      alert("Install metamask");
    }
    try {
      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });
      setWalletAddress(accounts[0]);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(async () => {
    const addr = localStorage.getItem("walletAddress");
    {
      addr && setWalletAddress(addr);
    }
    isWalletConnected();

    
    const response = await axios.get(
      `http://localhost:8080//tokenBalanceOf`,
      {
        params: { address: walletAddress},
      }
    );

    const balance = response.data.result/ 1000000000000000000;
    setWalletBalance(balance);

  }, [walletAddress]);

  return (
    <>
      <div className="navbar">
        <h2>NFT Marketplace</h2>
        <nav>
          <ul className="items">
            <li>
              <NavLink to="/" activeClassName="active">
                {" "}
                Explore{" "}
              </NavLink>
            </li>

            <li>
              <NavLink to="/popular" activeClassName="active">
                {" "}
                Popular{" "}
              </NavLink>
            </li>

            <li>
              <NavLink to="/sale" activeClassName="active">
                {" "}
                Sale{" "}
              </NavLink>
            </li>

            <li>
              <NavLink to="/collection" activeClassName="active">
                {" "}
                My Collection{" "}
              </NavLink>
            </li>

            <li>
              <NavLink to="/creators" activeClassName="active">
                {" "}
                Creators{" "}
              </NavLink>
            </li>
          </ul>
        </nav>

        <div className="buttons">
          {walletAddress ? (
            <>
              {" "}
              <NavLink to="/create">
                <button>
                  <span>Create</span>
                </button>
              </NavLink>
              <button>{walletBalance} <span>Tokens</span></button>
            </>
          ) : (
            <button type="button" onClick={connectWalletHandler}>
              <p>Connect Wallet</p>
            </button>
          )}
        </div>
      </div>
    </>
  );
};

export default Navbar;
