import { React, useEffect, useState } from "react";
import Dropzone from "react-dropzone";
import axios from "axios";
import "./create.css";

const Create = () => {
  const maxSize = 12000000;
  const [selectedFile, setSelectedFile] = useState(null);
  const [mintMessage, setMintMessage] = useState("");
  const [mintMessageState, setMintMessageState] = useState("success");

  const [walletAddress, setWalletAddress] = useState("");
  const [mintTitle, setMintTitle] = useState("");
  const [mintPrice, setMintPrice] = useState(0);
  const [dexcription, setDexcription] = useState("");
  const [mintCategory, setMintCategory] = useState("");

  const onDrop = (acceptedFile) => {
    if (acceptedFile.length == 0) {
      alert("File size must be less than 12mb");
    } else {
      setSelectedFile(acceptedFile);
      console.log(acceptedFile);
    }
  };


  const dropzoneStyle = {
    width  : "100%",
    height : "20%",
    border : "1px solid black"
};
  return (
    <div className="container">
      <h1>Create an NFT</h1>
      <form>
        <Dropzone
          className="dropzone"
          multiple={false}
          onDrop={onDrop}
          accept="video/*"
          maxSize={maxSize}
          style={dropzoneStyle}
        >
          {({ getRootProps, getInputProps, isDragActive, isDragReject }) => {
            return (
              <div {...getRootProps()}>
                <input {...getInputProps()} />
                {!isDragActive && "Click here or drop a file to upload!"}
                {isDragActive && !isDragReject && "Drop it!"}
                {isDragReject && "File type not accepted, sorry!"}
              </div>
            );
          }}
        </Dropzone>

        <input type={Text} placeholder="Title" />
        {/* Check to take it in number or text in API */}
        <input type={Text} placeholder="Price" />
        <textarea placeholder="Description"></textarea>
        <button>Mint</button>
      </form>
    </div>
  );
};

export default Create;
